import styled from 'styled-components';

export const container = styled.div`
    display: flex;
    flex-direction: column;
    width: 30vw;
    height: 80vh;
    background-color: #484f58;
    border-radius: 1rem;
    border: 1px solid silver;
    overflow: hidden;
`
export const question = styled.div`
    font-size: 1rem;
    text-align: center;
    width: 100%;
    height: 15vh;
    background-color: #484f58;
    border: 1px solid silver;
`
